mapper.select:
SELECT mr.v_route_code AS routeCode,
mr.v_route_name AS routeName
FROM tbl_mst_route mr


		mr.v_route_code = #{route_code}


ORDER BY mr.v_route_code;

SELECT mrs.v_route_code as routeCode, mrs.i_station_no as stationNo, mrs.v_station_name as stationName, mrs.dc_longitude AS lng, mrs.dc_latitude AS lat FROM tbl_mst_route_station mrs
AND mrs.v_route_code = #{route_code}
ORDER BY mrs.i_station_no;

SELECT a.v_route_code AS routeCode, DATE(a.dt_crtdt) AS run_date, a.i_shift_code, a.t_start_time, a.i_station_code AS stationNo, a.i_station_name AS stationName, a.dc_longitude AS lng, a.dc_latitude AS lat FROM tbl_biz_vehicle_arrive a
AND a.v_route_code = #{route_code}
AND DATE(a.dt_crtdt) = #{drv_date}
AND a.t_start_time = #{drv_time}
ORDER BY a.i_station_code
